import fetch from 'isomorphic-unfetch';

const API_BASE_URL = 'http://localhost:3001';

const baseRequest = (url, options) =>
    fetch(`${API_BASE_URL}${url}`, options);

const post = (url, body) =>
    baseRequest(url, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },

        body: JSON.stringify(body),
    });

export default post
