import fetch from 'isomorphic-unfetch';

const API_BASE_URL = 'http://localhost:3001';

export const post = (url, body) =>
    baseRequest(url, {
        cors: true,
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },

        body: JSON.stringify(body),
    });

export default post
